import {
  RECEIVE_ALL_BOOKS,
  REQUEST_ALL_BOOKS,
  ERROR_RECEIVE_ALL_BOOKS,
} from '../constants/actionTypes';

const initialState = {
  books: [],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case ERROR_RECEIVE_ALL_BOOKS: {
      return {
        ...state,
        isFailedFetchUser: true,
        isFetchingUser: false,
      };
    }

    case RECEIVE_ALL_BOOKS: {
      const {
        authorities,
        name,
      } = action.payload;

      return {
        ...state,
        authorities: authorities || initialState.authorities,
        name: name || initialState.name,
        isAuthorized: true,
        isFailedFetchUser: false,
        isFetchingUser: false,
      };
    }
    case REQUEST_ALL_BOOKS: {
      return {
        ...state,
        isFailedFetchUser: false,
        isFetchingUser: true,
      };
    }

    default: return state;
  }
}
