import {
  RECEIVE_ALL_BOOKS,
  REQUEST_ALL_BOOKS,
  ERROR_RECEIVE_ALL_BOOKS,
} from '../constants/actionTypes';

const initialState = {
  books: [{
    "id": 1,
    "title": "The Lord of the Rings123",
    "author": "J.R.R. Tolkien",
    "category": "Fiction"
  },],
};

export default (state = initialState, action) => {
  switch (action.type) {
    case ERROR_RECEIVE_ALL_BOOKS: {
      alert("ERROR_RECEIVE_ALL_BOOKS");
      return {
        ...state,
      };
    }

    case RECEIVE_ALL_BOOKS: {
      alert("RECEIVE_ALL_BOOKS");
      const books = action.payload;
      console.log(books);
      return {
        ...state,
        books: books || initialState.books,
      };
    }

    case REQUEST_ALL_BOOKS: {
      alert("REQUEST_ALL_BOOKS");
      return {
        ...state,
      };
    }

    default: return state;
  }
}
