import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector } from 'react-redux';
import Link from 'components/Link';
import Typography from 'components/Typography';
import useAccessValidate from 'hooks/useAccessValidate';

const GetEntitiesList = () => {
  const {
    BOOK_URL,
  } = config;

  //const books = getJson({url: `${BOOK_URL}`}).then(value => {return value});

  const books = getJson({url: `${BOOK_URL}`});

  return (
    <div>
      {books.then(value => value.map((book) => (
        <Link
          href={(location => ({
              ...location,
              pathname: `/${book}`,
              search: `${location.search}&id=42`,
            }))}
        >
          <Typography>
            {book}
          </Typography>
        </Link>
      )))}
    </div>
  )
};

export default GetEntitiesList;
