import {
  getJson,
  postJson,
} from 'requests';
import config from 'config/index';
import {
  RECEIVE_ALL_BOOKS,
  REQUEST_ALL_BOOKS,
  ERROR_RECEIVE_ALL_BOOKS,
} from '../constants/actionTypes';

const errorGetAllBooks = () => ({
  type: ERROR_RECEIVE_ALL_BOOKS,
});

const getAllBooks = () => {
  const {
    BOOKS_URL,
  } = config;
  return getJson({
    url: `${BOOKS_URL}`,
  })
  .then(value => console.log(value))
  .catch(() => {
    console.log("huyaks");
  });
};

const receiveAllBooks = (user) => ({
  type: RECEIVE_ALL_BOOKS,
  payload: user,
});

const requestAllBooks = () => ({
  type: REQUEST_ALL_BOOKS,
});

export const fetchBooks = () => (dispatch) => {
    dispatch(requestAllBooks());
    return getAllBooks({
      dispatch,
    }).then(user => dispatch(receiveAllBooks(user)))
      .catch(() => dispatch(errorGetAllBooks()));
};
