import {
  getJson,
  postJson,
} from 'requests';
import config from 'config/index';
import {
  RECEIVE_ALL_BOOKS,
  SUCCESS_DELETE_BOOK,
  REQUEST_ALL_BOOKS,
  REQUEST_DELETE_BOOK,
  ERROR_RECEIVE_ALL_BOOKS,
  ERROR_DELETE_BOOK
} from '../constants/actionTypes';

const errorGetAllBooks = () => ({
  type: ERROR_RECEIVE_ALL_BOOKS,
});


const getAllBooks = () => {
  const {
    BOOKS_URL,
  } = config;
  return getJson({
    url: `${BOOKS_URL}`,
  })
  .catch(() => {
    console.log("huyaks");
  });
};

const receiveAllBooks = (books) => ({
  type: RECEIVE_ALL_BOOKS,
  payload: books,
});

const requestAllBooks = () => ({
  type: REQUEST_ALL_BOOKS,
});

export const fetchBooks = () => (dispatch) => {
    dispatch(requestAllBooks());
    return getAllBooks({
      dispatch,
    }).then(books => dispatch(receiveAllBooks(books)))
      .catch(() => dispatch(errorGetAllBooks()));
};


const errorDeleteBook = () => ({
  type: ERROR_DELETE_BOOK,
});


const successDelteBook = (books) => ({
  type: SUCCESS_DELETE_BOOK,
  payload: books,
});

const requestDelteBook = () => ({
  type: REQUEST_DELETE_BOOK,
});

const deleteBook = (id) => {
  const {
    BOOKS_URL,
  } = config;
  return getJson({
    url: `${BOOKS_URL}/remove/${id}`,
  })
  .catch(() => {
    console.log("huyaks");
  });
};
